window.onload=function(){window.onscroll=function(){setStickyMenu()};var navbar=document.getElementById("stickyMenu");var sticky=navbar.offsetTop;function setStickyMenu(){if(window.pageYOffset>=sticky){navbar.classList.add("sticky")}else{navbar.classList.remove("sticky")}}};function responsiveMenu(){var x=document.getElementById("topnav");if(x.className==="navbar"){x.className+=" responsive"}else{x.className="navbar"}}
function retina(img,src){if(window.devicePixelRatio>1){src=src.replace(".png","@2x.png");img.onload=null;img.src=src}}
